package com.ruoyi.system.service.impl;import com.ruoyi.system.domain.Complain;import com.ruoyi.system.domain.Scenic;import com.ruoyi.system.mapper.ComplainMapper;import com.ruoyi.system.service.IComplainService;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import java.util.List;@Servicepublic class ComplainServiceImpl implements IComplainService {    @Autowired    private ComplainMapper complainMapper;    public Complain selectComplainById(Integer id){        return complainMapper.selectComplainById(id);    }    public List<Complain> selectComplainList(Complain complain){        return complainMapper.selectComplainList(complain);    }    public int insertComplain(Complain complain)    {return complainMapper.insertComplain(complain);}    public int updateComplain(Complain complain){        return complainMapper.updateComplain(complain);    }    public int deleteComplainByIds(Integer[] ids){        return complainMapper.deleteComplainByIds(ids);    }    public int deleteComplainById(Integer id){        return complainMapper.deleteComplainById(id);    }    @Override    public List<Complain> getAll() {        return complainMapper.getAll();    }}