<template>  <div class="details">    <div class="wrapper">      <h2 class="name" style="font-family: 'Volkhov', serif">{{ data.name }}</h2>      <el-row class="title" gutter="20" style="padding-top: 20px; padding-bottom: 20px; font-family: 'Lora', serif;">        <el-col class="oth" span="3">          {{data.location}}        </el-col>        <el-col class="oth" span="16">          评分：{{data.score}}        </el-col>      </el-row>      <div>        <img class="img" :src="data.pic"/>      </div>      <div>        <h2 class="oth" style="padding-left: 100px" >酒店简介</h2>        <p class="con" >{{data.description}}</p>      </div>    </div>    <div class="wrapper">      <h2 class="name">本店特色推荐</h2>      <div class="art">        <el-row :gutter="20">          <div              class="left"              v-for="item in findRestaurants"              :key="item.id"              @click="goDetail(item.id)"          >            <el-col :span="6" class="restaurant-card">              <div class="grid-content bg-purple">                <div class="pic"><img width="100%" :src="item.pic" class="restaurant-pic"/></div>                <div class="text">                  <div class="name2">小吃名：{{ item.name }}</div>                  <div class="name2">城市：{{ item.city }}</div>                  <div class="name2">价格：¥{{ item.price }}</div>                </div>                <div class="top"></div>              </div>            </el-col>          </div>        </el-row>      </div>    </div>  </div></template><script>import { getRestaurant, findTjFood } from "@/utils/request";export default {  name: "RestaurantDetail",  data() {    return {      id: "",      data: {},      queryParams: {        restaurant: null,      },      findRestaurants: []    };  },  methods: {    // 查询项目的详细信息    findDetails() {      // 数据交互      getRestaurant(this.id).then((res) => {        if (res.code == 200) {          this.data = res.data;          this.queryParams.restaurant=res.data.name;          findTjFood(this.queryParams).then((res) => {            if (res.code == 200) {              this.findRestaurants = res.data;            } else {              this.$message({                type: "error",                message: this.message,              });            }          });        } else {          this.$message({            type: "error",            message: res.msg,          });        }      });    },findPageQueryRestaurant() {    },    goDetail(id) {      // 跳转到详情页面，并且传递id      this.$router.push({ path: '/foodDetail', query: { id: id } });    }  },  created() {    // 在页面加载的时候拿到数据    // 拿到从上一个页面传过来的id值    this.id = this.$route.query.id;    this.findDetails();    this.findPageQueryRestaurant()  }};</script><style scoped lang='scss'>.wrapper {  padding-left: 300px;  .name {    font-size: 54px;    font-weight: bold;    margin-bottom: 10px;    font-family: 'Lora', serif;  }  .img {    width: 1080px;    height: auto;  }  .oth {    display: flex;    align-items: center;    font-size: 25px;    margin-bottom: 10px;    font-family: 'Volkhov', serif;  }  .con {    max-width: 860px; /* 设置最大宽度为860px */    /* 上下边距为0，左右边距自动，实现水平居中 */    margin: 0 auto;    font-size: 20px;    padding-right: 320px;    font-family: 'Volkhov', serif;  }}.restaurant-card {  border: 1px solid #ddd;  border-radius: 10px;  overflow: hidden;  margin-bottom: 20px;  cursor: pointer;  transition: transform 0.3s, box-shadow 0.3s;  font-family: 'Volkhov', serif;  background: #fff;  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);  &:hover {    transform: scale(1.05);    box-shadow: 0 8px 16px rgba(0, 0, 0, 0.2);  }  .restaurant-pic {    height: 150px;    object-fit: cover;    border-bottom: 1px solid #ddd;  }  .text {    padding: 10px;    .name2 {      font-size: 16px;      margin-bottom: 5px;      font-weight: bold;    }  }}</style>